package com.poynt.payment.app.ui;import android.annotation.SuppressLint;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.TextView;import com.poynt.payment.app.R;import com.poynt.core.CardStateHandler;import com.poynt.core.base.BaseModelFragment;public class ModelP5Fragment extends BaseModelFragment {    private static final String TAG = "ModelP5Fragment";    private static CardStateHandler mCardStateableHandler;    public TextView mApplicationStatus;    public Button mButtonNext;    public Button mButtonOk;    public Button mButtonCancel;    private ModelP5ViewImpl mMobileP5ViewImpl;    @SuppressLint("ValidFragment")    private ModelP5Fragment(){};    private static class LazyModelP5FragmentHolder {        static final ModelP5Fragment INSTANCE = new ModelP5Fragment();    }    public static ModelP5Fragment getInstance(CardStateHandler stateHolder) {        mCardStateableHandler = stateHolder;        return LazyModelP5FragmentHolder.INSTANCE;    }    @Override    public View onCreateView(@NonNull LayoutInflater inflater,                             @Nullable ViewGroup container,                             @Nullable Bundle savedInstanceState) {        View retView = inflater.inflate(R.layout.model_p5_view, container, false);        initializeUI(retView);        return retView;    }    void initializeUI(View view) {        mApplicationStatus = view.findViewById(R.id.main_activity_status);        mButtonNext = view.findViewById(R.id.main_activity_button_next);        mButtonNext.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                mMobileP5ViewImpl.setNewState();            }        });        mButtonCancel = view.findViewById(R.id.main_activity_button_cancel);        mButtonCancel.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                mMobileP5ViewImpl.setCancelState();            }        });        mButtonOk = view.findViewById(R.id.main_activity_button_ok);        mButtonOk.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                mMobileP5ViewImpl.setWelcomeState();            }        });        mMobileP5ViewImpl = new ModelP5ViewImpl(this, mCardStateableHandler);    }    @Override    public void onResume() {        super.onResume();        mMobileP5ViewImpl.onViewResumed();    }    @Override    public void onPause() {        super.onPause();        mMobileP5ViewImpl.onViewPaused();    }    @Override    public void onStop() {        super.onStop();    }    @Override    public void onDestroy() {        super.onDestroy();    }}