package com.poynt.payment.app.presenter;import com.poynt.core.enums.TransactionState;import com.poynt.payment.app.listener.ModelP61View;import com.poynt.payment.app.ui.ModelP61Fragment;public class CardInsertedStatePresenter implements IPresenter{    public static ModelP61View mMainViewListener;    private ModelP61Fragment mMainView;    private CardInsertedStatePresenter(){        mMainView = mMainViewListener.getModelP61FragmentView();    }    @Override    public void updateUI() {        mMainView.mApplicationStatus.setText("Card Inserted");    }    @Override    public boolean isYourState(int state) {        if(state == TransactionState.CARD_INSERTED)            return true;        return false;    }    private static class LazyCardInsertedStatePresenter {        static final CardInsertedStatePresenter INSTANCE = new CardInsertedStatePresenter();    }    public static CardInsertedStatePresenter getInstance(ModelP61View view) {        mMainViewListener = view;        return LazyCardInsertedStatePresenter.INSTANCE;    }    public void showCardInserted() {    }}