package com.poynt.payment.app.presenter;import com.poynt.core.enums.TransactionState;import com.poynt.payment.app.listener.ModelP61View;import com.poynt.payment.app.ui.ModelP61Fragment;public class CardReadingStatePresenter implements IPresenter{    public static ModelP61View mMainViewListener;    private ModelP61Fragment mMainView;    private CardReadingStatePresenter() {        mMainView = mMainViewListener.getModelP61FragmentView();    }    @Override    public void updateUI() {        mMainView.mApplicationStatus.setText("Card Reading");    }    @Override    public boolean isYourState(int state) {        if(state == TransactionState.CARD_READING)            return true;        return false;    }    private static class LazyCardReadingStatePresenter {        static final CardReadingStatePresenter INSTANCE = new CardReadingStatePresenter();    }    public static CardReadingStatePresenter getInstance(ModelP61View view) {        mMainViewListener = view;        return LazyCardReadingStatePresenter.INSTANCE;    }    public void showCardReading() {    }}