package com.poynt.payment.app.presenter;import com.poynt.core.enums.TransactionState;import com.poynt.payment.app.listener.ModelP61View;import com.poynt.payment.app.ui.ModelP61Fragment;public class TransactionFailedPresenter implements IPresenter{    public static ModelP61View mMainViewListener;    private ModelP61Fragment mMainView;    private TransactionFailedPresenter() {        mMainView = mMainViewListener.getModelP61FragmentView();    }    @Override    public void updateUI() {        mMainView.mApplicationStatus.setText("Card Transaction Failed");    }    @Override    public boolean isYourState(int state) {        if(state == TransactionState.TRANSACTION_CANCELED)            return true;        return false;    }    private static class LazyCardTransactionFailedPresenter {        static final TransactionFailedPresenter INSTANCE = new TransactionFailedPresenter();    }    public static TransactionFailedPresenter getInstance(ModelP61View view) {        mMainViewListener = view;        return LazyCardTransactionFailedPresenter.INSTANCE;    }    public void showCardTransactionFailed() {    }}