package com.poynt.payment.app.presenter;import com.poynt.core.enums.TransactionState;import com.poynt.payment.app.listener.ModelP61View;import com.poynt.payment.app.ui.ModelP61Fragment;public class TransactionFinishedPresenter implements IPresenter{    public static ModelP61View mMainViewListener;    private ModelP61Fragment mMainView;    private static int myState = TransactionState.TRANSACTION_FINISHED;    private TransactionFinishedPresenter() {        mMainView = mMainViewListener.getModelP61FragmentView();    }    @Override    public void updateUI() {        mMainView.mApplicationStatus.setText("Card Transaction Success");    }    @Override    public boolean isYourState(int state) {        if(myState == state)            return true;        return false;    }    private static class LazyCardTransactionFinishedPresenter {        static final TransactionFinishedPresenter INSTANCE = new TransactionFinishedPresenter();    }    public static TransactionFinishedPresenter getInstance(ModelP61View view) {        mMainViewListener = view;        return LazyCardTransactionFinishedPresenter.INSTANCE;    }    public void showCardTransactionFinished() {    }}