package com.poynt.payment.app.presenter;import com.poynt.core.enums.TransactionState;import com.poynt.payment.app.listener.ModelP61View;import com.poynt.payment.app.ui.ModelP61Fragment;public class TransactionInProgressPresenter implements IPresenter{    public static ModelP61View mMainViewListener;    private ModelP61Fragment mMainView;    private TransactionInProgressPresenter() {        mMainView = mMainViewListener.getModelP61FragmentView();    }    @Override    public void updateUI() {        mMainView.mApplicationStatus.setText("Card Processing");    }    @Override    public boolean isYourState(int state) {        if(state == TransactionState.TRANSACTION_IN_PROGRESS)            return true;        return false;    }    private static class LazyCardTransactionInProgressPresenter {        static final TransactionInProgressPresenter INSTANCE = new TransactionInProgressPresenter();    }    public static TransactionInProgressPresenter getInstance(ModelP61View view) {        mMainViewListener = view;        return LazyCardTransactionInProgressPresenter.INSTANCE;    }    public void showCardTransactionProgress() {    }}