package com.poynt.payment.app.presenter;import com.poynt.core.enums.TransactionState;import com.poynt.payment.app.listener.ModelP61View;import com.poynt.payment.app.ui.ModelP61Fragment;public class WelcomeStatePresenter implements IPresenter{    public static ModelP61View mMainViewListener;    private ModelP61Fragment mMainView;    private static int myState = TransactionState.WELCOME_STATE;    private WelcomeStatePresenter(){        mMainView = mMainViewListener.getModelP61FragmentView();    }    @Override    public void updateUI() {        mMainView.mApplicationStatus.setText("Welcome to Poynt !");    }    @Override    public boolean isYourState(int state) {        if(myState == state){            return true;        }        return false;    }    private static class LazyWelcomeStatePresenter {        static final WelcomeStatePresenter INSTANCE = new WelcomeStatePresenter();    }    public static WelcomeStatePresenter getInstance(ModelP61View view) {        mMainViewListener = view;        return LazyWelcomeStatePresenter.INSTANCE;    }    public void updateWelcomeScreen() {    }}