package com.poynt.payment.app.ui;import android.annotation.SuppressLint;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.TextView;import com.poynt.payment.app.R;import com.poynt.core.CardStateHandler;import com.poynt.core.base.BaseModelFragment;public class ModelP61Fragment extends BaseModelFragment {    private static final String TAG = "ModelP61Fragment";    private static CardStateHandler mCardStateableHandler;    public TextView mApplicationStatus;    public Button mButtonNext;    public Button mButtonOk;    public Button mButtonCancel;    private ModelP61ViewImpl mMobileP61ViewImpl;    @SuppressLint("ValidFragment")    private ModelP61Fragment(){};    private static class LazyMobileFragmentHolder {        static final ModelP61Fragment INSTANCE = new ModelP61Fragment();    }    public static ModelP61Fragment getInstance(CardStateHandler stateHolder) {        mCardStateableHandler = stateHolder;        return LazyMobileFragmentHolder.INSTANCE;    }    @Override    public View onCreateView(@NonNull LayoutInflater inflater,                             @Nullable ViewGroup container,                             @Nullable Bundle savedInstanceState) {        View retView = inflater.inflate(R.layout.model_p61_view, container, false);        initializeUI(retView);        return retView;    }    void initializeUI(View view) {        mApplicationStatus = view.findViewById(R.id.main_activity_status);        mButtonNext = view.findViewById(R.id.main_activity_button_next);        mButtonNext.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                mMobileP61ViewImpl.setNewState();            }        });        mButtonCancel = view.findViewById(R.id.main_activity_button_cancel);        mButtonCancel.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                mMobileP61ViewImpl.setCancelState();            }        });        mButtonOk = view.findViewById(R.id.main_activity_button_ok);        mButtonOk.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                mMobileP61ViewImpl.setWelcomeState();            }        });        mMobileP61ViewImpl = new ModelP61ViewImpl(this, mCardStateableHandler);    }    @Override    public void onResume() {        super.onResume();        mMobileP61ViewImpl.onViewResumed();    }    @Override    public void onPause() {        super.onPause();        mMobileP61ViewImpl.onViewPaused();    }    @Override    public void onStop() {        super.onStop();    }    @Override    public void onDestroy() {        super.onDestroy();    }}