package com.poynt.core.cardstates;import com.poynt.core.di.CoreComponent;import com.poynt.core.enums.TransactionState;import com.poynt.log.Logger;public class WelcomeScreenState extends BaseCardState {    private static final String TAG = "WelcomeScreenState";    public WelcomeScreenState(){};    private static class LazyCardWelcomeScreenStateHolder {        static final WelcomeScreenState INSTANCE = new WelcomeScreenState();    }    public static WelcomeScreenState getInstance() {        return LazyCardWelcomeScreenStateHolder.INSTANCE;    }    @Override    protected void injectDependencies() {        CoreComponent.getComponentInstance().inject(this);    }    @Override    public void onStateApplied() {        super.onStateApplied();        Logger.d(TAG, "onStateApplied");        getActionInterface().notifyOnStateEntered(TransactionState.WELCOME_STATE);    }    @Override    public void onStateLeft() {        super.onStateLeft();        Logger.d(TAG, "onStateLeft");        getActionInterface().notifyOnStateLeft(TransactionState.WELCOME_STATE);    }    @Override    public void showNextEvent() {        launchStateForEventID(TransactionState.CARD_INSERTED);    }}