package com.poynt.log;import com.poynt.lib.BuildConfig;public class Logger {    private static final String DEFAULT_TAG = "StateMachine";    private static LogHandler logHandler;    private static boolean enabled = false;    private Logger() {        logHandler = new DefaultLogHandler();        if(BuildConfig.DEBUG) {            enabled = true;        }    }    private static class LazyLoggerHolder {        static final Logger INSTANCE = new Logger();    }    public static Logger getInstance() {        return LazyLoggerHolder.INSTANCE;    }    public static boolean isEnabled() {        return enabled;    }    public static void setEnabled(boolean enabled) {        if (BuildConfig.DEBUG) {            Logger.enabled = enabled;        }    }    public static void d(String message) {        d(DEFAULT_TAG, message);    }    public static void d(String tag, String message) {        if (enabled) {            logHandler.d(tag, message);        }    }    public static void i(String message) {        i(DEFAULT_TAG, message);    }    public static void i(String tag, String message) {        if (enabled) {            logHandler.i(tag, message);        }    }    public static void v(String message) {        v(DEFAULT_TAG, message);    }    public static void v(String tag, String message) {        if (enabled) {            logHandler.v(tag, message);        }    }    public static void w(String message) {        w(DEFAULT_TAG, message);    }    public static void w(String tag, String message) {        if (enabled) {            logHandler.w(tag, message);        }    }    public static void e(String message) {        e(DEFAULT_TAG, message);    }    public static void e(String tag, String message) {        if (enabled) {            logHandler.e(tag, message);        }    }    public static void e(Throwable e) {        e(e.getMessage(), e);    }    public static void e(String message, Throwable e) {        e(DEFAULT_TAG, message, e);    }    public static void e(String tag, String message, Throwable e) {        if (enabled) {            logHandler.e(tag, message, e);        }    }}